import React, { useEffect, useState } from "react";
import Quote from "./Quote";
import "./App.css";
const App = () => {
  const [quotes, setQuotes] = useState([]);
  const apiUrl = "https://type.fit/api/quotes";

  //Run on page load
  useEffect(() => {
    getQuots([]);
  }, []);

  //Fetch from the API
  const getQuots = async () => {
    setQuotes();
    const request = await fetch(apiUrl);
    const data = request.json();
    setQuotes(data);
  };

  console.log(quotes);

  return (
    <div className="App">
      <form className="search-form">
        <input className="search-bar" type="text" />
        <button className="search-btn" type="submit">
          Search
        </button>
      </form>
      <List
        size="small"
        loading={isLoading}
        header={
          <Button
            onClick={() => getQuotes()}
            type="primary"
            icon={<DownloadOutlined />}
            disabled={isLoading}
            size="large"
          >
            Fetch Quotes
          </Button>
        }
        footer={<a href={repoUrl}>Fork on Github</a>}
        bordered
        dataSource={quotes}
        renderItem={(quote) => (
          <List.Item>
            <Quote text={quote.text} author={quote.author} />
          </List.Item>
        )}
      />
    </div>
  );
};
export default App;

