import React, { useEffect, useState } from "react";
import styled from "styled-components";

function Random() {
  const [popular, setPopular] = useState([]);

  useEffect(() => {
    getPopular();
  }, []);

  const getPopular = async () => {
    const api = await fetch(
      "https://game-of-thrones-quotes.herokuapp.com/v1/random/3"
    );
    const data = await api.json();
    setPopular(data);
    console.log(data);
  };
  let image;
  return (
    <div>
      <h1>RANDOM GOT</h1>
      {popular.map((line) => {
        const images = line.character.house.slug;
        //start the switch
        switch (images) {
          case "lannister":
            line.image =
              "https://static.wikia.nocookie.net/gameofthrones/images/8/8a/House-Lannister-Main-Shield.PNG";
            break;
          case "baratheon":
            line.image =
              "https://static.wikia.nocookie.net/gameofthrones/images/0/00/House-Baratheon-Main-Shield.PNG";
            break;
          case "stark":
            line.image =
              "https://static.wikia.nocookie.net/gameofthrones/images/8/8a/House-Stark-Main-Shield.PNG";
            break;
          default:
            console.log(`No image for ${images}.`);
        }

        return (
          <div key={line.sentence}>
            <p>{line.sentence}</p>
            <img src={line.image} alt="" />
            <h3>------</h3>
          </div>
        );
      })}
    </div>
  );
}

export default Random;
