import React, { useEffect, useState } from "react";
import styled from "styled-components";

function Random() {
  const [popular, setPopular] = useState([]);

  useEffect(() => {
    getPopular();
  }, []);

  const getPopular = async () => {
    const api = await fetch(
      "https://game-of-thrones-quotes.herokuapp.com/v1/random/3"
    );
    const data = await api.json();
    setPopular(data);
    console.log(data);
  };
  let image;
  return (
    <div>
      <h1>RANDOM GOT</h1>
      {popular.map((line) => {
        if (line.character.house.slug == "lannister") {
          line.image =
            "https://static.wikia.nocookie.net/gameofthrones/images/8/8a/House-Lannister-Main-Shield.PNG";
        }
        return (
          <div key={line.sentence}>
            <p>{line.sentence}</p>
            <img src={line.image} alt="" />
          </div>
        );
      })}
    </div>
  );
}

export default Random;
