import Got from "../components/Got";
import Popular from "../components/Popular";
import Breaking from "../components/Breaking";
import styled from "styled-components";
import React, { useState, useEffect } from "react";

function Home() {
  const [stateB, setStateB] = useState(false);
  const [stateG, setStateG] = useState(false);
  const [clear, setclear] = useState(false);

  //Make UseEffect on a refresh button, fetch only first time.

  function renderMe(show) {
    if (show === "G") {
      setStateG(true);
      setStateB(false);
      setclear(true);
    } else if (show === "B") {
      setStateG(false);
      setStateB(true);
      setclear(true);
    }
  }
  let initialState = () => {
    setStateG(false);
    setStateB(false);
    setclear(false);
  };
  return (
    <Body>
      <Title>
        <h1>API APP!</h1>
      </Title>
      <button onClick={() => renderMe("G")}>GOT</button>
      <button onClick={() => renderMe("B")}>BRBA</button>
      {clear === true && (
        <button id="clear-btn" onClick={() => initialState()}>
          Clear
        </button>
      )}

      <Main>
        <Container>
          <Popular />
          {stateG === true && <Got />}
          {stateB === true && <Breaking />}
        </Container>
      </Main>
    </Body>
  );
}
const Main = styled.div``;
const Body = styled.div`
  margin: auto;
`;

const Title = styled.div`
  padding-bottom: 30px;
`;

const Container = styled.div`
  margin: auto;
  padding: 15px;
  border: 0.3rem solid darkkhaki;
  border-radius: 1rem;
  max-width: 90%;
  max-height: 800px;
  overflow: scroll;
  overflow-x: hidden;
  scroll-behavior: smooth;
`;

export default Home;
